import { EmptyState, Layout, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import React from 'react';
import './style.css';
import Modal from 'react-awesome-modal';


const img = 'https://media3.dealerdecoque.fr/themes/ddcV2/img/logo_ddc.svg';

class Index extends React.Component {
  state = { open: false };
constructor(props) {
    super(props);
    this.state = {
        visible : false
    }
}

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }
  state = { open: false };
  render() {
    return (
      //test
      <div>
      <Page>
        <Layout>
        <html>
          <body>

            <div class="navbar">
              <button class="btn"><i class="fa fa-refresh"></i> Settings</button>
              <button class="btn"><i class="fa fa-cog"></i> Settings</button>
            </div>

            <div class="head">
              <h1 id="titre-page">
                Ajouter des produits à envoyer à notre équipe
              </h1>
              <p class="ssTitre">Vous êtes à quelques pas de la personnalisation</p>
            </div>

            <div id="centre">
              <img src="https://img.icons8.com/ios/452/t-shirt--v1.png" alt="" id="img_center"></img>
              <div class="center">
                <input type="button" value="Open" onClick={() => this.openModal()} />
                  <Modal visible={this.state.visible} width="800" height="600" effect="fadeInUp"
                    onClickAway={() => this.closeModal()}>
                    <div>
                      <h1>Title</h1>
                      <p>Some Contents</p>
                      <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                        <div class="card">
                            <img src="img_avatar.png" alt="Avatar"></img>
                              <div class="container">
                                <h4><b>John Doe</b></h4>
                                  <p>Architect & Engineer</p>
                                </div>
                              </div>
                    </div>
                  </Modal>
              </div>
            </div>

            <hr></hr>

            <div>
              <img src="https://media2.dealerdecoque.fr/img/dealer-de-coque-logo-15936736591.jpg" alt="" id="logo_dealer"></img>
              <p>Dealer de Coque <br></br> 2020 - Emilien et Thomas</p>

            </div>

          </body>
        </html>
        </Layout>
      </Page >
      </div>



    );
  }
  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false })
    console.log(idsFromResources)

  };

}

export default Index;
