import { EmptyState, Layout, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import React from 'react';
import './style.css';
import Modal from 'react-awesome-modal';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'

const img = 'https://media3.dealerdecoque.fr/themes/ddcV2/img/logo_ddc.svg';



class Index extends React.Component {
  state = { open: false };
constructor(props) {
    super(props);
    this.state = {
        visible : false
    }
}

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }
  state = { open: false };
  render() {
    return (
      //test
      <div>
      <Page>
        <Layout>
        <html>
          <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <body>

            <div class="navbar">
              <button class="btn"><i class="fa fa-refresh"></i> Refresh</button>
              <button class="btn"><i class="fa fa-cog"></i> Settings</button>
            </div>

            <div class="head">
              <h1 id="titre-page">
                Ajouter des produits à envoyer à notre équipe
              </h1>
              <p class="ssTitre">Vous êtes à quelques pas de la personnalisation</p>
            </div>

            <div id="centre">
              <img src="https://img.icons8.com/ios/452/t-shirt--v1.png" alt="" id="img_center"></img>
              <div class="center">
                <Button variant="primary" type="button" class="button" value="Open" onClick={() => this.openModal()}>Open </Button>
                  <Modal {...props} aria-labelledby="contained-modal-title-vcenter">
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Using Grid in Modal
        </Modal.Title>
      </Modal.Header>
      <Modal.Body className="show-grid">
        <Container>
          <Row>
            <Col xs={12} md={8}>
              .col-xs-12 .col-md-8
            </Col>
            <Col xs={6} md={4}>
              .col-xs-6 .col-md-4
            </Col>
          </Row>

          <Row>
            <Col xs={6} md={4}>
              .col-xs-6 .col-md-4
            </Col>
            <Col xs={6} md={4}>
              .col-xs-6 .col-md-4
            </Col>
            <Col xs={6} md={4}>
              .col-xs-6 .col-md-4
            </Col>
          </Row>
        </Container>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
                </div>
            </div>

            <hr></hr>

            <div>
              <img src="https://media2.dealerdecoque.fr/img/dealer-de-coque-logo-15936736591.jpg" alt="" id="logo_dealer"></img>
              <p>Dealer de Coque <br></br> 2020 - Emilien et Thomas</p>

            </div>
          </body>

        </html>
        </Layout>
      </Page >
      </div>



    );
  }
  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false })
    console.log(idsFromResources)

  };

}



export default Index;
