import { EmptyState, Layout, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import React from 'react';
import './style.css';
import Modal from 'react-awesome-modal';


const img = 'https://media3.dealerdecoque.fr/themes/ddcV2/img/logo_ddc.svg';
const portalRoot = typeof document !== `undefined` ? document.getElementById('portal') : null


filterSelection("all")
function filterSelection(c) {
  var x, i;
  x = document.getElementsByClassName("filterDiv");
  if (c == "all") c = "";
  for (i = 0; i < x.length; i++) {
    w3RemoveClass(x[i], "show");
    if (x[i].className.indexOf(c) > -1) w3AddClass(x[i], "show");
  }
}


function w3AddClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    if (arr1.indexOf(arr2[i]) == -1) {element.className += " " + arr2[i];}
  }
}

function w3RemoveClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    while (arr1.indexOf(arr2[i]) > -1) {
      arr1.splice(arr1.indexOf(arr2[i]), 1);
    }
  }
  element.className = arr1.join(" ");
}

// Add active class to the current button (highlight it)
var btnContainer = document.getElementById("myBtnContainer");
var btns = btnContainer.getElementsByClassName("btn");
for (var i = 0; i < btns.length; i++) {
  btns[i].addEventListener("click", function(){
    var current = document.getElementsByClassName("active");
    current[0].className = current[0].className.replace(" active", "");
    this.className += " active";
  });
}


class Index extends React.Component {
  state = { open: false };
constructor(props) {
    super(props);
    this.state = {
        visible : false
    }
}

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }
  state = { open: false };
  render() {
    return (
      //test
      <div>
      <Page>
        <Layout>
        <html>
          <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <body>

            <div class="navbar">
              <button class="btn"><i class="fa fa-refresh"></i> Settings</button>
              <button class="btn"><i class="fa fa-cog"></i> Settings</button>
            </div>

            <div class="head">
              <h1 id="titre-page">
                Ajouter des produits à envoyer à notre équipe
              </h1>
              <p class="ssTitre">Vous êtes à quelques pas de la personnalisation</p>
            </div>

            <div id="centre">
              <img src="https://img.icons8.com/ios/452/t-shirt--v1.png" alt="" id="img_center"></img>
              <div class="center">
                <input type="button" value="Open" onClick={() => this.openModal()} />
                  <Modal visible={this.state.visible} width="1500" height="600" effect="fadeInUp"
                    onClickAway={() => this.closeModal()}>
                    <div>
                      <a href="javascript:void(0);" onClick={() => this.closeModal()}><i class="fa fa-close"></i></a>


                        <h2>Filter DIV Elements</h2>

                        <div id="myBtnContainer">
                          <button class="btn active" onclick="filterSelection('all')"> Show all</button>
                          <button class="btn" onclick="filterSelection('cars')"> Cars</button>
                          <button class="btn" onclick="filterSelection('animals')"> Animals</button>
                          <button class="btn" onclick="filterSelection('fruits')"> Fruits</button>
                          <button class="btn" onclick="filterSelection('colors')"> Colors</button>
                        </div>

                        <div class="container">
                          <div class="filterDiv">BMW</div>
                          <div class="filterDiv">Orange</div>
                          <div class="filterDiv cars">Volvo</div>
                          <div class="filterDiv colors">Red</div>
                          <div class="filterDiv cars animals">Mustang</div>
                          <div class="filterDiv colors">Blue</div>
                          <div class="filterDiv animals">Cat</div>
                          <div class="filterDiv animals">Dog</div>
                          <div class="filterDiv fruits">Melon</div>
                          <div class="filterDiv fruits animals">Kiwi</div>
                          <div class="filterDiv fruits">Banana</div>
                          <div class="filterDiv fruits">Lemon</div>
                          <div class="filterDiv animals">Cow</div>
                        </div>
                    </div>
                  </Modal>
              </div>
            </div>

            <hr></hr>

            <div>
              <img src="https://media2.dealerdecoque.fr/img/dealer-de-coque-logo-15936736591.jpg" alt="" id="logo_dealer"></img>
              <p>Dealer de Coque <br></br> 2020 - Emilien et Thomas</p>

            </div>
          </body>

        </html>
        </Layout>
      </Page >
      </div>



    );
  }
  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false })
    console.log(idsFromResources)

  };

}



export default Index;
