import { EmptyState, Layout, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import React from 'react';
import './style.css';
import Modal from 'react-awesome-modal';


const img = 'https://media3.dealerdecoque.fr/themes/ddcV2/img/logo_ddc.svg';

// Add active class to the current button (highlight it)
if (typeof document !== 'undefined') {
var btnContainer = document.getElementById("myBtnContainer");
var btns = btnContainer.getElementsByClassName("btn");
for (var i = 0; i < btns.length; i++) {
  btns[i].addEventListener("click", function(){
    var current = document.getElementsByClassName("active");
    current[0].className = current[0].className.replace(" active", "");
    this.className += " active";
  });
}}


class Index extends React.Component {
  state = { open: false };
constructor(props) {
    super(props);
    this.state = {
        visible : false
    }
}

    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }
  state = { open: false };
  render() {
    return (
      //test
      <div>
      <Page>
        <Layout>
        <html>
          <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <body>

            <div class="navbar">
              <button class="btn"><i class="fa fa-refresh"></i> Refresh</button>
              <button class="btn"><i class="fa fa-cog"></i> Settings</button>
            </div>

            <div class="head">
              <h1 id="titre-page">
                Ajouter des produits à envoyer à notre équipe
              </h1>
              <p class="ssTitre">Vous êtes à quelques pas de la personnalisation</p>
            </div>

            <div id="centre">
              <img src="https://img.icons8.com/ios/452/t-shirt--v1.png" alt="" id="img_center"></img>
              <div class="center">
                <input type="button" class="button" value="Open" onClick={() => this.openModal()} />
                  <Modal visible={this.state.visible} width="1500" height="600" effect="fadeInUp"
                    onClickAway={() => this.closeModal()}>
                    <div>
                      <a href="javascript:void(0);" onClick={() => this.closeModal()}><i class="fa fa-close"></i></a>

                    <div id="buttons">
                      <button class="btn_filter">all</button>
                      <button class="btn_filter">Tshirt</button>
                      <button class="btn_filter">Cap</button>
                    </div>
                    <script src="script.js"></script>

                    <div class="objects">
                      <div class="box">
                            <div class="card">
                              <div class="container">
                                <img class="imageproduit" src="https://www.eminence.fr/img/i-5164-10/t-shirt_col_v_luxor_eminence_-_coton_-_blanc_47.jpg" alt="Avatar"></img>
                                <h4><b>Tee-Shirt</b></h4>
                                  <p>Homme - personnalisable</p>
                              </div>
                            </div>
                            </div>
                          <div class="box">
                            <div class="card">
                              <div class="container">
                                <img class="imageproduit" src="https://www.headict.com/img/products2/product/FIT0002_1,flexfit.jpg" alt="Avatar"></img>
                                <h4><b>Casquette</b></h4>
                                  <p>Mixte - personnalisable</p>
                              </div>
                            </div>
                          </div>
                    </div>
                    </div>
                  </Modal>
              </div>
            </div>

            <hr></hr>

            <div>
              <img src="https://media2.dealerdecoque.fr/img/dealer-de-coque-logo-15936736591.jpg" alt="" id="logo_dealer"></img>
              <p>Dealer de Coque <br></br> 2020 - Emilien et Thomas</p>

            </div>
          </body>

        </html>
        </Layout>
      </Page >
      </div>



    );
  }
  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false })
    console.log(idsFromResources)

  };

}



export default Index;
