import { EmptyState, Layout, Page } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import React from 'react';
import './style.css';

const img = 'https://media3.dealerdecoque.fr/themes/ddcV2/img/logo_ddc.svg';

class Index extends React.Component {
  state = { open: false };
  render() {
    return (
      //test
      <Page>
        <html>
          <body>

            <div class="head">
              <h1 id="titre-page">
                Ajouter des produits pour la personnalisation
              </h1>
              <p class="ssTitre">Vous etes Ã  quelques pas de la personnalisation</p>
            </div>

            <div id="centre">
              <image src="logo_ddc.svg" alt="logo dealer"></image>
              <button class="btn_addproduct" type="button">Add products!</button>

                <a href="#" class="action-button shadow animate blue">Hello</a>
                <a href="#" class="action-button shadow animate red">How</a>
                <a href="#" class="action-button shadow animate green">Are</a>
                <a href="#" class="action-button shadow animate yellow">You?</a>


            </div>

          </body>
        </html>
      </Page >
    );
  }
  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false })
    console.log(idsFromResources)

  };

}

export default Index;
